svg.script:
var TPI = 2 * Math.PI;
var n = 6;
var R = 126;
var r = 25.5;
var cX = 250;
var cY = 250;
var T = 150;
var t = T / n;

var primaryGear;
var secondaryGear;
var transmitter;
var hourHand;

function start(evt) {
  primaryGear = evt.target.ownerDocument.getElementById("primary_gear");
  secondaryGear = evt.target.ownerDocument.getElementById("secondary_gear");
  transmitter = evt.target.ownerDocument.getElementById("transmitter");
  hourHand = evt.target.ownerDocument.getElementById("hour_hand");
  animate();

  // Refresh every second.
  setInterval(animate, 100);
}

function animate() {

  // Get computer clock time
  var date = new Date();

  var s = date.getSeconds() + date.getMilliseconds() / 1000;
  var thetaDegree = s / 60 * 360;
  var thetaRadian = (thetaDegree / 360) * TPI;
  var x = cX + (R + r) * Math.cos(thetaRadian - Math.PI / 2);
  var y = cY + (R + r) * Math.sin(thetaRadian - Math.PI / 2);

  // rotation of the second wheel + space between teeths
  var secondaryTheta = thetaDegree * n + (2 * 360 / t) + 1;
  secondaryGear.setAttribute("transform", "translate(" + x + "," + y + ") rotate( " + secondaryTheta + ")");

  var hourTheta = thetaDegree * n;
  hourHand.setAttribute("transform", "translate(" + x + "," + y + ") rotate( " + hourTheta + ")");

  transmitter.setAttribute("transform", "rotate(" + (thetaDegree + 1) + ", " + cX + "," + cY + ")");
}

window.animate = animate
